import React, { Component } from 'react';

import { connect } from 'react-redux';
import { addTaskAction } from './actions/tasksActions';

import Header from './components/headerComponent';
import Task from './components/taskComponent';



const mapStateToProps = (state) => ({
  tasks: state.tasksReducer.tasks
});

const mapDispatchToProps = (dispatch) => ({
  addTaskAction: (id,status,description,time) => dispatch(addTaskAction(id,status,description,time))
});

class App extends Component {

  addTask = () => {

    this.props.addTaskAction(
      Math.random(),
      'A',
      'completed',
      '10:AM'
    );

  }

  render() {  
    const { tasks } = this.props;

    const tasksList = tasks.map( task => {
      return (
        <div className="task" key={ task.id }>
          <Task id={task.id} description={task.description} status={task.status} time={task.time} />
        </div>
      )
    });

    return (
      <div className="App">
        <div align="center">
          <Header />
        </div>
        <div>
          {tasksList}
        </div>
        <div>
          <button onClick={this.addTask}>Add</button>
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(App);
