import React, { Component } from 'react';

import { connect } from 'react-redux';
import { deleteTaskAction, completeTaskAction } from '../actions/tasksActions';
import { Icon, Button, Input } from 'react-materialize';

const mapStateToProps = (state) => ({
    tasks: state.tasksReducer.tasks
});

const mapDispatchToProps = (dispatch) => ({
    deleteTask: (id) => dispatch(deleteTaskAction(id)),
    completeTask: (id) => dispatch(completeTaskAction(id))
});

class Task extends Component {

  deleteTask = () => {
    this.props.deleteTask(this.props.id);
  }

  completeTask = () => {
    this.props.deleteTask(this.props.id);
  }

  render() {  
    console.log(this.props);
    const { description, status, time } = this.props;
    return (
      <tr className="taskItem">
          <td>{ description }</td>
          <td>{ time } </td>
          <td>{ status } </td>
          <td>
            <Input name='group1' type='checkbox' value='green' label='Green' className='filled-in' defaultChecked='checked' />
            <Button small floating onClick={this.deleteTask}><Icon>delete_forever</Icon></Button>
          </td>
      </tr>
    );
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Task);
