import React, { Component } from 'react';

import { connect } from 'react-redux';
import { addTaskAction } from './actions/tasksActions';

import Header from './components/headerComponent';
import Task from './components/taskComponent';



const mapStateToProps = (state) => ({
  tasks: state.tasksReducer.tasks
});

const mapDispatchToProps = (dispatch) => ({
  addTaskAction: (id,status,description,time) => dispatch(addTaskAction(id,status,description,time))
});

class App extends Component {

  state = {
    id:0,
    description:'',
    time:'',
    status:'pending'    
  };

  addTask = (e) => {

    this.props.addTaskAction(
      (this.props.tasks.length+1),
      'A'+Math.floor(Math.random()*10),
      'completed',
      '10:AM'
    );
    e.preventDefault();

  }

  handleChange = (e) => {
    this.setState({
      id:(this.tasks.length+1),
      [e.target.id]: e.target.value,
      status:'pending'
    });
  }

  render() {  
    const { tasks } = this.props;

    const tasksList = tasks.map( task => {
      return (
        <div className="task" key={ task.id }>
          <Task id={task.id} description={task.description} status={task.status} time={task.time} />
        </div>
      )
    });

    return (
      <div className="App">
        <div align="center">
          <Header />
        </div>
        <div>
          {tasksList}
        </div>
        <div>
          <form>
          <input type="text" id="description" onChange={this.handleChange}/>
          <input type="text" id="time" onChange={this.handleChange}/>
            <button onClick={this.addTask}>Add</button>
          </form>
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(App);
