import React, { Component } from 'react';

import { connect } from 'react-redux';
import { addTaskAction } from './actions/tasksActions';

import Header from './components/headerComponent';
import Task from './components/taskComponent';

import { Row, Col, Icon, Button, Input, Table } from 'react-materialize';
import profile_img from './images/profile.png';


import Lottie from 'react-lottie';
import  animationData from './images/ciclista_salita.json';

const mapStateToProps = (state) => ({
  tasks: state.tasksReducer.tasks
});

const mapDispatchToProps = (dispatch) => ({
  addTaskAction: (id,status,description,time) => dispatch(addTaskAction(id,status,description,time))
});

class App extends Component {

  state = {
    id:0,
    description:'',
    time:'',
    status:'pending'    
  };

  addTask = (e) => {

    this.props.addTaskAction(
      (this.props.tasks.length+1),
      this.state.status,
      this.state.description,
      this.state.time
    );
    e.preventDefault();

  }

  handleChange = (e) => {
    this.setState({
      id:(this.props.tasks.length+1),
      [e.target.id]: e.target.value,
      status:'pending'
    });
  }

  render() {  
    const { tasks } = this.props;

    const tasksList = tasks.map( task => {
      return (
          <Task id={task.id} description={task.description} status={task.status} time={task.time} />
      )
    });

    return (
      <div className="App">
        <Row>
          <Col s={12} align="center">      
            <Lottie options={{
                loop: true,
                autoplay: true, 
                animationData: animationData,
                rendererSettings: {
                    preserveAspectRatio: 'xMidYMid slice'
                }
            }}
                height={200}
                width={200}
            />  
            <Header />
          </Col>
          <Col s={4}>
            <Row>
              <Col s={12} align="center">
                <img alt="profile" src={profile_img} />
              </Col>
              <Col s={12} align="center">
                <h5>Juan dela Cruz</h5>
              </Col>
              <Col s={12} align="center">
                React Developer
              </Col>
            </Row>
          </Col>
          <Col s={8}>
            <Table bordered={true}>
              <thead>
                <tr>
                  <th>Description</th>
                  <th>Time</th>
                  <th>Status</th>
                  <th width="60"></th>
                </tr>
              </thead>
              <tbody>
                {tasksList}
              </tbody>
            </Table>
            <br/>
            <form onSubmit={this.addTask} id="form">
              <Row>
                <Input placeholder="Enter Description" s={6} label="Description" type="text" id="description" onChange={this.handleChange}><Icon>speaker_notes</Icon></Input>
                <Input placeholder="Enter Time" s={4} label="Time" type="text" id="time" onChange={this.handleChange}><Icon>access_time</Icon></Input>
                <Button s={2} floating small className='red' waves='light' icon='add' />
              </Row>
            </form>
          </Col>
        </Row>

      </div>
    );
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(App);
