export default (state = {
    tasks:[
        {
            'id': 1,
            'status':'pending',
            'description':'Eat',
            'time':'11:00 AM'
        }
    ]
}, action) => {
    console.log(action);
    switch (action.type) {
        case 'ADD_TASK':
        var tasks = state.tasks.push(action.payload);
        console.log(tasks);
            return {
                result: {...state, tasks:  tasks }
            }
        case 'DELETE_TASK':
            return {
                result: action.payload
            }
        case 'COMPLETE_TASK':
            return {
                result: action.payload
            }
        default:
            return state
    }
}
